import React, {useRef, useState} from 'react';
import cl from '../css/Question1.module.css';
import image from '../images/3.jpg';

const Question1 = () => {
    const [iterator, setIterator] = useState(5);
    let img = useRef();
        


    return (
        <div className={cl.container}>
            <h1 className={cl.task__one}>Задание 1</h1>
            <img src={image} className={cl.image} ref={img}/>
            <h2 className={cl.increase__border__button}>Increase/decrease border button</h2>
            <div className={cl.btn__wrapper}>
                <button className={cl.btn}>Increase</button>
                <button className={cl.btn}>Decrease</button>
            </div>
        </div>
    );
};

export default Question1;
